import React, {useState} from 'react';
import './App.css';

function App() {

  const [state, setState] = useState({input: "", select: ""});
  const [response, setResponse] = useState({response: ""});
  function changeSubmit(event){
    async function getData(){
        let response = await fetch("http://localhost:5000",{method: "POST", headers:{"Content-Type": "application/json"}, body: JSON.stringify(state)});
        response = await response.json();
        setResponse(response)
    }
    getData()
    event.preventDefault();
  }

  function changeInput(event){
      if (event.target.id === "entity")
        {
          setState((prev) => {prev.input = event.target.value; return prev;})
        }
      else
        {
          setState((prev) => {prev.select = event.target.value; return prev;})
        }
      console.log(state);
  }

  return (
    <div>
      <h1>Dark web sentiment analysis</h1>
      <form onSubmit={changeSubmit}>
        <h3>Get major sentiment for entity.</h3>
        <label for="entity">Entity:</label>
        <input type="text" name="entity" id="entity" placeholder='Ex: Entity' onChange={changeInput}></input>
        <h3>Get meaningful articles for sentiment.</h3>
        <label for="sentiment">Sentiment:</label>
        <select onChange={changeInput}>
          <option>Positive</option>
          <option>Negative</option>
        </select>
        <button type="Submit" value="Submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
